function run(DWNT, contact_identity, tunnel_originator, random_guid) {
    try {
        DWNT = JSON.parse(DWNT|| "{}");

        DWNT.journey = random_guid;

        if (DWNT.contact === undefined) DWNT.contact = {};
        if (DWNT.contact.source === undefined) DWNT.contact.source = contact__source(contact_identity);
        if (DWNT.contact.phone === undefined) DWNT.contact.phone = contact__phone(contact_identity);
        if (DWNT.contact.DDD === undefined) DWNT.contact.DDD = contact__DDD(contact_identity);
        if (DWNT.contact.id === undefined) DWNT.contact.id = tunnel_originator ? tunnel_originator : contact_identity;
        DWNT.contact.new = DWNT.contact.new === undefined;

		DWNT.error = null;
        DWNT.checkpoints = [];

    } catch(err) {}
    return JSON.stringify(DWNT);
}

function contact__source(contactIdentity) {
    contactIdentity = String(contactIdentity);
    if (contactIdentity.includes("wa.gw.msging.net")) {
        return "WHATSAPP";
    } else if (contactIdentity.includes("messenger")) {
        return "FACEBOOK";
    } else if (contactIdentity.includes("instagram")) {
        return "INSTAGRAM";
    } else if (contactIdentity.includes("businessmessages")) {
        return "GOOGLE_BUSINESS_MESSAGE";
    } else if (contactIdentity.includes("mailgun")) {
        return "E_MAIL";
    } else if (contactIdentity.includes("0mn") || contactIdentity.includes("blip")) {
        return "BLIP_CHAT";
    } else {
        return "OUTRO";
    }
}

function contact__phone(contactIdentity) {
    contactIdentity = String(contactIdentity);
    try {
        if (!contact__source(contactIdentity) !== "WHATSAPP") {
            throw new Error("Not a whatsapp contact");
        }
        return String(contactIdentity).split("@")[0];
    } catch(err) {
        return "Indefinido";
    }  
}

function contact__DDD(contactIdentity) {
    contactIdentity = String(contactIdentity);
    try {
        if (!contact__source(contactIdentity) !== "WHATSAPP") {
            throw new Error("Not a whatsapp contact");
        }
        return String(contactIdentity).split("@")[0].substring(2, 4);
    } catch(err) {
        return "Indefinido";
    }  
}

function run(DWNT, errorType, errorMessage, stateId, stateName, inputContent) {
	let error = {};
	try {
		DWNT = JSON.parse(DWNT || "");
		let errorDescription = "Not defined";
		if (String(errorType).startsWith("SCRIPT_ERROR")) {
			errorDescription = errorType.replace("SCRIPT_ERROR ", "");
			errorType = "SCRIPT_ERROR";
		}
		error["type"] = errorType || "UNEXPECTED_INPUT";
		error["message"] = errorMessage || null;
		error["description"] = errorDescription;
		error["state"] = {
			"id": stateId, 
			"name": stateName, 
			"bot": DWNT.application["bot"],
			"service": DWNT.application["service"]
		};
		error["input"] = DWNT.input;
		error["return"] = {"state": stateId, "bot": null, "service": DWNT.application["service"]};
		const recurrence = {"state": 1, "type": 1};
		if (DWNT.error && DWNT.error["state"] && DWNT.error["type"]) {
			if (DWNT.error["state"]["id"] == error["state"]["id"]) {
				recurrence["state"] = DWNT.error["recurrence"]["state"] + 1;
			}
			if (DWNT.error["type"] == error["type"]) {
				recurrence["type"] = DWNT.error["recurrence"]["type"] + 1;
			}
		}
		error["recurrence"] = recurrence;

		// Checkpoints
		if (error["type"] == "GO_BACK") {
			error["return"] = get_last_checkpoint(DWNT, error);
			if (DWNT["application"]["bot"] == error["return"]["bot"]) {
				error["return"]["bot"] = null;
			}
			error["message"] = null;
		}

		if (inputContent.startsWith("#goto")) {
			const id = inputContent.replace("#goto ", "");
			error["type"] = "GO_TO";
			error["message"] = null;
			error["return"]["state"] = id;
		}

	} catch(err) {
		error = {
			"type": "UNEXPECTED_INPUT",
			"message": null,
			"return": {"state": stateId, "bot": null},
			"err": String(err)
		};
	}
	return JSON.stringify(error);
}


function get_last_checkpoint(DWNT, error) {
	let return_info = error["return"];
	if (Array.isArray(DWNT.checkpoints) && DWNT.checkpoints.length > 0) {
		return_info = DWNT.checkpoints[0];
	}
	return return_info;
}

/**
 * Decodifica o erro registrado em informa√ß√µes de:
 * - Mensagem que ser√° mostrada
 * - Necessidade de parada do fluxo
 * - Id de retorno caso em caso de reentrada
 * 
 * @param {{type: string, message: string, input: {content: string, type: string, group: string}, state: {id: string, name: string}, recurrence: {state: number, type: number}, return: {state: string, bot: string}}} error Error pr√©-preenchido.
 * 
 * @returns {{type: string, message: string, input: {content: string, type: string, group: string}, state: {id: string, name: string}, recurrence: {state: number, type: number}, return: {state: string, bot: string}}} Erro final
 */
function process_error(error) {

	if (error.message == null) {
		error["message"] = get_content_message(error.input.group);
	}

	switch(error.type) {
		case "GO_BACK":
		case "GO_TO":
			error["message"] = null;
			break;
		case "INACTIVITY":
			error["message"] = "Voc√™ est√° h√° alguns minutos sem enviar mensagens, ent√£o vou encerrar a conversa. Assim que puder voltar a conversar, √© s√≥ me chamar üòâ!";
			error["return"]["state"] = null;
			break;
		case "CLOSE":
			error["message"] = "Obrigado por nos contatar! üòä Se precisar de mais ajuda, n√£o hesite em nos procurar novamente.";
			error["return"]["state"] = null;
			break;
		case "API":
			error["message"] = "Desculpe, houve um erro no sistema. üôÅ Estamos resolvendo! Por favor, tente novamente em alguns minutos.";
			error["return"]["state"] = null;
			break;
		case "SCRIPT_ERROR":
			error["message"] = "Desculpe, algo inesperado aconteceu. üôÅ Estamos resolvendo! Por favor, tente novamente em alguns minutos.";
			error["return"]["state"] = null;
			break;
	}
	return error;
}

function get_content_message(group) {
	let message;
	if (group == "audio") {
		message = "N√£o consigo receber √°udio ainda! Por favor, responda apenas com texto ou toque em uma das op√ß√µes do menu."
	} else if (group == "image") {
		message = "N√£o consigo receber imagens ainda! Por favor, responda apenas com texto ou toque em uma das op√ß√µes do menu."
	} else if (group == "video") {
		message = "N√£o consigo receber v√≠deo ainda! Por favor, responda apenas com texto ou toque em uma das op√ß√µes do menu."
	} else if (group == "document") {
		message = "N√£o consigo receber documentos ainda! Por favor, responda apenas com texto ou toque em uma das op√ß√µes do menu."
	} else if (group == "location") {
		message = "N√£o consigo receber localiza√ß√£o ainda! Por favor, responda apenas com texto ou toque em uma das op√ß√µes do menu."
	} else {
		message = "Desculpe, n√£o consegui compreender. Tente novamente."
	}
	return message;
}

function run(error, stateId) {
	try {
		error = JSON.parse(error || '');
		error = process_error(error);
	} catch(err) {
		error = {
			"type": "UNEXPECTED_INPUT",
			"message": "Desculpe, n√£o consegui compreender.",
			"return": {"state": stateId, "bot": null},
			"description": String(err)
		};
	}
	return JSON.stringify(error);
}

function run(DWNT, error) {
	try {
		DWNT = JSON.parse(DWNT);
		error = JSON.parse(error);
		DWNT.error = error;
		DWNT.fromError = true;
		// checkpoint
		if (error["type"] == "GO_BACK") {
			DWNT.checkpoints.shift();
			DWNT.hasCheckpoints = DWNT.checkpoints.length > 0 || undefined;
		}
	} catch(err) {
		DWNT = {err: String(err)};
	}
	return JSON.stringify(DWNT);
}

