function run(clientCpfCnpj) {
    try {
        const sanitizedInput = clientCpfCnpj.replace(/[^\d]+/g, '');

        function isValidCPF(cpf) {
            if (cpf.length !== 11) {
                return false;
            }
            if (/^(\d)\1+$/.test(cpf)) {
                return false;
            }

            return true;
        }

        function isValidCNPJ(cnpj) {
            if (cnpj.length !== 14) {
                return false;
            }
            if (/^(\d)\1+$/.test(cnpj)) {
                return false;
            }

            return true;
        }

        if (sanitizedInput.length === 11) {
            return isValidCPF(sanitizedInput) ? sanitizedInput : false;
        } else if (sanitizedInput.length === 14) {
            return isValidCNPJ(sanitizedInput) ? sanitizedInput : false;
        } else {
            return false;
        }
    } catch (e) {
        return e.message;
    }
}
