const HELP_MESSAGE = `Commands:

üîπ goto {block_id}: jump to block block_id.
‚û°Ô∏è Example: <b>goto onboarding</b>

üîπ redirect {service_name}: redirect to service service_name.
‚û°Ô∏è Example: <b>redirect main</b>

üîπ print {type}[contact | context | services]: print contact, context or services.
‚û°Ô∏è Example: <b>print contact; print context; print services</b>
<b>Print service caveats:</b>
Context size can lead to reduced responses, thus you can use take and skip parameters
‚û°Ô∏è Example: <b>print context {skip} {take}</b>

üîπ set {type}[contact | context] {key} {value}: set key to value on contact or context. 
‚û°Ô∏è Example: <b>set context a 1</b>
<b>Set contact caveats:</b>
When key is json defined the command will produce a set contact but the value <b>must be JSON string</b>
‚û°Ô∏è Example: <b>set contact {"name":"test"}</b>
When key is a contact property the command will produce a merge contact. 
‚û°Ô∏è Example: <b>set contact name test; set contact extras.key 1</b>

üîπ get {type}[context] {key}: get variavble key from context.
‚û°Ô∏è Example: <b>get context a</b>

üîπ merge {type}[contact] json {value}: merge value on contact. Value <b>must be JSON string</b>
‚û°Ô∏è Example: <b>merge contact json {"name":"test"}</b>

üîπ delete {type}[context] {key}: delete key on context. 
‚û°Ô∏è Example: <b>delete context a</b>
`;

function decodeCommand(command, context) {
    const cmd = { 
        definition: command.join(" "),
        args: command,
        id: null,
        data: null,
        priority: 0,
        message: null
    };

    let type = String(command[0]).toUpperCase();
    let subtype = String(command[1] || "").toUpperCase();
    let key = String(command[2] || "");
    let value = String(command[3] || "");

    let data = {};
    const identity = context["identity"];

    switch(type) {
        case "#DEBUG":
            cmd["id"] = "DEBUG";
            cmd["data"] = command[1];
            break;
        case "SET":
            if (subtype == "CONTACT") {
                cmd["id"] = "MERGE_CONTACT";
                if (key.toUpperCase() == "JSON") {
                    cmd["id"] = "SET_CONTACT";
                    data = getContactData(identity, value);
                } else if (key.startsWith("extras.")) {
                    const subkey = key.split("extras.")[1];
                    data = {extras: {}};
                    data["extras"][subkey] = value;
                } else {
                    data[key] = value;
                }
                data["identity"] = identity;
                cmd["data"] = data;
            } else if (subtype == "CONTEXT") {
                cmd["id"] = "SET_CONTEXT";
                cmd["data"] = { "identity": identity, "key": key, "value": value };
            }
            cmd["priority"] = 1;
            break;
        case "GET":
            if (subtype == "CONTEXT") {
                cmd["id"] = "GET_CONTEXT";
                cmd["data"] = { "identity": identity, "key": key };
            }
            cmd["priority"] = 2;
            break;
        case "MERGE":
            if (subtype == "CONTACT" && key.toUpperCase() == "JSON") {
                cmd["id"] = "MERGE_CONTACT";
                data = getContactData(identity, value);
                cmd["data"] = data;
            }
            cmd["priority"] = 3;
            break;
        case "DELETE":
            if (subtype == "CONTEXT") {
                cmd["id"] = "DELETE_CONTEXT";
                cmd["data"] = { "identity": identity, "key": key };
            } 
            cmd["priority"] = 4;
            break;
        case "HELP":
            cmd["id"] = "HELP";
            cmd["message"] = HELP_MESSAGE;
            cmd["priority"] = 5;
            break;
        case "PRINT":
            if (subtype == "CONTEXT") {
                cmd["id"] = "PRINT_CONTEXT";
                cmd["data"] = {"take": key || 200, "skip": value || 0 };
            } else if (subtype == "CONTACT") {
                cmd["id"] = "PRINT_CONTACT";
            } else if (subtype == "SERVICES") {
                cmd["id"] = "PRINT_SERVICES";
            }
            cmd["priority"] = 6;
            break;
        case "REDIRECT":
            cmd["id"] = "REDIRECT";
            cmd["data"] = command[1];
            cmd["priority"] = 7;
            break;
        case "GOTO":
            cmd["id"] = "GOTO";
            cmd["data"] = command[1];
            cmd["message"] = `üîÅ Jumping to block ${cmd["data"]}`;
            cmd["priority"] = 8;
            break;
        case "CLOSE":
            cmd["id"] = "CLOSE";
            break; 
    }

    return cmd;
}

function decodeArgs(command) {
    const [command, json] = command.split(/[\t ]+json[\t ]+/i);
    const args = command.split(/[\t ]+/);
    if (json) {
        args.push("json", json);
    }
    return args
        .map(chunk => chunk.trim())
        .filter(chunk => chunk.length > 0);
}

function parseCommands(inputContent, context) {
    let commands;

    commands = String(inputContent || "")
        .split(/[;\n]/)
        .map(decodeArgs)
        .filter(cmd => cmd.length > 0);

    commands = commands
        .map(cmd => decodeCommand(cmd, context))
        .filter(cmd => cmd.id !== null);

    if (commands.some(cmd => ["PRINT_SERVICES", "REDIRECT"].includes(cmd["id"]))) {
        commands.push({id: "GET_SERVICES"});
    }

    if (commands.some(cmd => cmd["id"] == "CLOSE") && !commands.some(cmd => cmd["id"] == "GOTO")) {
        commands.push({id: "GOTO", data: "onboarding", message: "üîÅ Jumping to block onboarding", priority: 8});
    }

    commands = commands
        .reduce(function (acc, cmd) {
            acc[cmd.id] = cmd;
            return acc;
        }, {});

    return commands
}

function run(input, contactId, secret, commands) {
    secret = secret || "123";

    const session = JSON.parse(commands || "{}").DEBUG;

    const context = {identity: contactId};
    commands = parseCommands(input, context);

    commands["DEBUG"] = commands["DEBUG"] || session;
    commands["ENABLED"] = commands.DEBUG && commands.DEBUG.data == secret;

    return JSON.stringify(commands);
}

