const MAX_STR_LEN = 500;

function printObject(data, level) {
	return Object
        .keys(data)
        .map(function(key) {
            const value = data[key];
            let msg = new Array(level).fill("-").join("") + " ";
            if (["string", "boolean", "number"].includes(typeof value)) {
                let val = String(value);
                if (val.length >= MAX_STR_LEN) {
                    val = val.slice(0, MAX_STR_LEN) + "...";
                }
                msg += `<b>${key}:</b> ${val}`;
            } else {
                msg += `<b>${key}:</b>\n${printObject(value, level + 1)}`;
            }
            return msg;
        })
        .join("\n");
}

