function run(DWNT, application_identifier, input_content, input_type, intents, random_guid) {
    try {
        DWNT = JSON.parse(DWNT|| "{}");

        const now = new Date();
        DWNT.datetime = datetime__to_string(now, -3);
        DWNT.date = String(DWNT.datetime).split(" ")[0];
        DWNT.time = String(DWNT.datetime).split(" ")[1];

        DWNT.states = {
            "fallback": "fallback"
        };

        if (DWNT.journey == undefined) DWNT.journey = random_guid;

        DWNT.mainService = "Main";
        DWNT.application = {
            "bot": application_identifier, 
            "service": "Main"
        };
        DWNT.application["isMain"] = DWNT.mainService == DWNT.application["service"];

        DWNT.input = {};
        DWNT.input.content = input_content;
        DWNT.input.type = input_type;
        DWNT.input.group = input__group(input_type, input_content);
        DWNT.input.intent = input__intent(input_content, intents);
        DWNT.input.event = input__event(DWNT.input.intent, intents);

        DWNT.hasCheckpoints = Array.from(DWNT.checkpoints || []).length > 0 || undefined;
        DWNT.disableCheckpoints = undefined;
        DWNT.disableInactivity = undefined;
        DWNT.disableClose = undefined;

        DWNT.fromError = false;

    } catch(err) {
        DWNT = { err: String(err) };
    }
    return JSON.stringify(DWNT);
}

function datetime__to_string(date, tzOffset){
    tzOffset = tzOffset || -3;
    const dateTz = new Date(new Date(date).getTime() + tzOffset * 60 * 60 * 1000) ;
    return dateTz.toISOString().replace("T", " ").split(".")[0];
}

function input__event(intent, intents) {
    if (!intents || typeof intent !== 'string') return null;
    return intent ? intent : "Inesperado";
}

function input__intent(inputContent, intents) {
    if (!intents) return null;
    try {
        intents = JSON.parse(intents);
    } catch(err) {
        return "FAILED_TO_PARSE_INTENTS";
    }
    if (Array.isArray(intents)) {
        return input__intent_array(inputContent, intents);
    } {
        return input__intent_obj(inputContent, intents);
    }
}

function input__intent_obj(inputContent, intents) {
    try {
        for (let intent in intents) {
            if (intents.hasOwnProperty(intent)) {
                let matches = intents[intent];
                if (!Array.isArray(matches)) {
                    matches = [matches];
                }
                const matched = matches.some(regexp => new RegExp("^" + regexp + "$", "i").test(inputContent));
                if (matched) return intent;
            }
        }
    } catch(err) {
        return "FAILED_TO_PROCESS_OBJ_INTENT: " + String(err);
    }
    return null;
}

function input__intent_array(inputContent, intents) {
    try{
        for (let i = 0; i < intents.length; i ++) {
            let matches = intents[i]['matches'];
            let intent = intents[i]['intent'];
            if (!Array.isArray(matches)) {
                matches = [matches];
            }
            const matched = matches.some(regexp => new RegExp("^" + regexp + "$", "i").test(inputContent));
            if (matched) return intent;
        }
    } catch(err) {
        return "FAILED_TO_PROCESS_ARRAY_INTENT: " + String(err);
    }
    return null;
}

function input__group(inputType, inputContent) {
    let _type = String(inputType || "");
    if (_type == "text/plain") {
        return "text";
    }
    try {
        const content = JSON.parse(inputContent);
        _type = String(content.type || "");
    } catch(err) {
        _type = "";
    }
    if (_type.includes("image") || _type.includes("sticker")) {
        return "image";
    }
    if (_type.includes("video")) {
        return "video";
    }
    if (_type.includes("audio")) {
        return "audio";
    }
    if (_type.includes("pdf")) {
        return "document";
    }
    if (_type.includes("location")) {
        return "location";
    }
    return null;
}
